documentation.xhtml:
i_runCW : BOOL;
i_runCCW : BOOL;
i_reset : BOOL;
i_targetVelocity : INT;

ST.xhtml:
THIS^.q_driveReset := isError;
IF NOT isError THEN
	errorReset := TRUE;
END_IF

THIS^.q_driveReset := isError;
IF NOT isError THEN
	errorReset := TRUE;
END_IF

IF (i_accRamp <> q_accRamp) OR (i_dccRamp <> q_accRamp) THEN
	wasChanged := TRUE;
ELSE
	wasChanged := FALSE;
END_IF

IF i_setPosition >= 0 THEN
	THIS^.q_controlWord.4 := TRUE;
	THIS^.q_controlWord.5 := FALSE;
	THIS^.q_frequencyCommand := i_setPosition;
ELSE
	THIS^.q_controlWord.4 := FALSE;
	THIS^.q_controlWord.5 := TRUE;
	THIS^.q_frequencyCommand := -i_setPosition;
END_IF

IF THIS^.i_warnErrorCode <> 0 THEN
	isError := TRUE;
ELSE
	isError := FALSE;
END_IF

IF THIS^.i_outputFrequency = 0 THEN
	isSpindleStopped := TRUE;
ELSE
	isSpindleStopped := FALSE;
END_IF

THIS^.q_controlWord.7 := isError;
IF NOT isError THEN
	errorReset := TRUE;
END_IF

readRamps := TRUE; IF readRamps AND readRampsDone THEN IF THIS^.i_accTime <> TO_REAL(accTimeRead/100) OR THIS^.i_dccTime <> TO_REAL(dccTimeRead/100) THEN wasChanged := TRUE; ELSE wasChanged := FALSE; END_IF readRamps := FALSE; END_IF
ReadAccRamp( xExecute:= readRamps, usiCom:= 1, uiDevice:= THIS^.i_deviceECAdress, usiChannel:= 1, wIndex:= 16#3001, bySubindex:= 16#D, udiTimeOut:= 100, pBuffer:= ADR(accTimeRead), szSize:= SIZEOF(accTimeRead), xDone=> , xBusy=> , xError=> , eError=> , udiSdoAbort=> , szDataRead=> );
ReadDccRamp( xExecute:= ReadAccRamp.xDone, usiCom:= 1, uiDevice:= THIS^.i_deviceECAdress, usiChannel:= 1, wIndex:= 16#3001, bySubindex:= 16#E, udiTimeOut:= 100, pBuffer:= ADR(dccTimeRead), szSize:= SIZEOF(dccTimeRead), xDone=> readRampsDone, xBusy=> , xError=> , eError=> , udiSdoAbort=> , szDataRead=> );

accRamp := TO_DINT(THIS^.i_maxVelocity / THIS^.i_accTime); dccRamp := TO_DINT(THIS^.i_maxVelocity / THIS^.i_dccTime); writeAcc := TRUE; IF WriteDccRamp.xDone THEN writeRamps := TRUE; writeAcc := FALSE; ELSE writeRamps := FALSE; END_IF
WriteAccRamp( xExecute:= writeAcc, usiCom:= 1, uiDevice:= THIS^.i_deviceECAdress, usiChannel:= 1, wIndex:= 16#604F, bySubindex:= 16#0, udiTimeOut:= 100, pBuffer:= ADR(accRamp), szSize:= SIZEOF(accRamp) );
WriteDccRamp( xExecute:= WriteAccRamp.xDone, usiCom:= 1, uiDevice:= THIS^.i_deviceECAdress, usiChannel:= 1, wIndex:= 16#6050, bySubindex:= 16#0, udiTimeOut:= 100, pBuffer:= ADR(dccRamp), szSize:= SIZEOF(dccRamp) );

update();

CASE status OF
	SpindleMaganerStatus_e.INIT:
		initial();
	SpindleMaganerStatus_e.STANDBY:
		standby();
	SpindleMaganerStatus_e.POWER_ON:
		powerOn();
	SpindleMaganerStatus_e.WORKING_CW:
		workingCW();
	SpindleMaganerStatus_e.WORKING_CCW:
		workingCCW();
	SpindleMaganerStatus_e.STOPPING:
		stopping();
	SpindleMaganerStatus_e.ERROR:
		error();
END_CASE

THIS^.status := THIS^.nextStatus;

IF isError THEN
	nextStatus := SpindleMaganerStatus_e.ERROR;
END_IF

IF wasChanged() THEN
	writeRamps();
END_IF

THIS^.q_error := isError;
THIS^.q_spindleStopped := isSpindleStopped;
THIS^.q_spindleTargetReached := isSpindleTargetReached;

IF THIS^.i_runCW OR THIS^.i_runCCW THEN
	THIS^.nextStatus := SpindleMaganerStatus_e.POWER_ON;
END_IF

IF NOT isPowerOn THEN
	setPowerOn();
ELSE
	IF THIS^.i_runCW THEN
		nextStatus := SpindleMaganerStatus_e.WORKING_CW;
	ELSIF THIS^.i_runCCW THEN
		nextStatus := SpindleMaganerStatus_e.WORKING_CCW;
	ELSE
		nextStatus := SpindleMaganerStatus_e.STOPPING;
	END_IF
END_IF

runSpindle(THIS^.i_targetVelocity);
IF THIS^.i_runCCW THEN
	nextStatus := SpindleMaganerStatus_e.WORKING_CCW;
ELSIF NOT THIS^.i_runCW AND NOT THIS^.i_runCCW THEN
	nextStatus := SpindleMaganerStatus_e.STOPPING;
END_IF

runSpindle(-THIS^.i_targetVelocity);
IF THIS^.i_runCW THEN
	nextStatus := SpindleMaganerStatus_e.WORKING_CW;
ELSIF NOT THIS^.i_runCW AND NOT THIS^.i_runCCW THEN
	nextStatus := SpindleMaganerStatus_e.STOPPING;
END_IF

IF NOT isSpindleStopped THEN
	stopSpindle();
ELSE
	setPowerOff();
END_IF

IF NOT isPowerOn THEN
	THIS^.nextStatus := SpindleMaganerStatus_e.STANDBY;
END_IF

IF THIS^.i_reset THEN
	THIS^.nextStatus:= SpindleMaganerStatus_e.INIT;
END_IF
stopSpindle();
setPowerOff();
